mapper.select:
select
a.`id`, a.`name`, u.`name` as `owner`, a.`startDate`, a.`endDate`
from tbl_activity a inner join tbl_user u on a.`owner` = u.`id`
a.`name` like '%' #{name} '%'
and u.`name` = #{owner}
and a.`startDate` >= #{startDate}
and a.`endDate` <= #{endDate}
order by a.`createTime` desc

select count(*) from (

select

a.`id`

from tbl_activity a
inner join tbl_user u
on a.`owner` = u.`id`




        a.`name` like '%' #{name} '%'



        and u.`name` = #{owner}



        and a.`startDate` >= #{startDate}



        and a.`endDate` <= #{endDate}




order by a.`createTime` desc

) tbl_activity
